        /// <summary>
        /// The maximum amount of money she can spend on a keyboard and USB drive, or -1 if she can't purchase both items
        /// </summary>
        /// <param name="keyboards">array of Integers</param>
        /// <param name="drives">array of Integers</param>
        /// <param name="money">money in Int</param>
        /// <returns></returns>
        private static int getMoneySpent(int[] keyboards, int[] drives, int money)
        {
            try
            {
                int moneySpent = -1, keyboardsSet = keyboards.Length, drivesSet = drives.Length, currentMax = -1;
                if (money < 2 || keyboardsSet < 1 || drivesSet < 1)
                    moneySpent = -1;
                else
                {
                    for(int i=0;i<keyboardsSet;i++)
                    {
                        for(int j=0;j<drivesSet;j++)
                        {
                            if (keyboards[i] + drives[j] <= money)
                            {
                                currentMax = keyboards[i] + drives[j];
                                moneySpent = (currentMax > moneySpent) ? currentMax : moneySpent;   //Using ternary operator here to assign max amt which can be spend to far
                            }
                        }
                    }
                }
                return moneySpent;
            }
            catch(Exception e)
            {
                Console.WriteLine("Error encoutnered : {0}",e.Message);
                return -1;
            }
        }
